/*global chrome*/
import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  //Creating a new state.
  let [input, setInput] = useState("");
  var num = 0;
  //Funtion that will execute when input is changed.
  function Search(e) {
    //Storing the input by the user into the state.
    input = e.target.value;
    console.log(input);
    //Sending messages to the contentScript.
    chrome.tabs.query({ active: true, currentWindow: true }, function (tabs) {
      chrome.tabs.sendMessage(tabs[0].id, { from: "App", subject: input });
    });

    chrome.runtime.onMessage.addListener((msg, sender, response) => {
      if (msg.from === "Content") {
        console.log("MESSAGE FROM BACKGROUND:" + msg.from);
        num = msg.num;
      }
    });
  }
  return (
    <body className="App-body">
      <div className="container">
        Enter word to search:
        <div className="input">
          <input
            type="text"
            id="search"
            placeholder="Enter word"
            onChange={Search}
          ></input>
          <div className="content">
            <p>
              '{input}' is present in {num}
            </p>
          </div>
        </div>
      </div>
    </body>
  );
}

export default App;
