import React, { useEffect } from "react";
import Test from "./components/box";
import "./App.css";

function App() {
  //Initiating the state.
  const [state, stateFunction] = React.useState("State");
  //Function that changes the value of the state.
  function handleClick() {
    stateFunction((prev) => {
      //Ternary operator.
      return prev === "State" ? "No" : "State";
    });
  }
  useEffect(() => {
    const title = document.getElementById("title");
    var clicked = false;
    document.getElementById("button").addEventListener("click", () => {
      if (!clicked) {
        console.log("back");
        title.style["color"] = "black";
        clicked = true;
      } else if (clicked) {
        console.log("White");
        title.style["color"] = "white";
        clicked = false;
      }
    });
  }, []);
  return (
    <body className="App-body">
      <div className="App">
        <h3 id="title">The below is the value of the state.</h3>
        <p onClick={handleClick}>{state}</p>
        <Test name="Philip" surname="Grima" />
      </div>
    </body>
  );
}

export default App;
